/**
 * Represents the content of an SVG file.
 */
declare module "*.svg" {
  const content: string;
  export default content;
}

/**
 * Represents the content of a PNG file.
 */
declare module "*.png" {
  const content: string;
  export default content;
}

/**
 * Represents the content of a JPG file.
 */
declare module "*.jpg" {
  const content: string;
  export default content;
}
